const clickData = $input.item.json;
const emailToFind = $('Set_Click').first().json.email
const currentUrl = $('Set_Click').first().json.url;

const sheetRows = $items("Data_Sheet1");
let matchedRow = null;
let matchIndex = -1;

for (let i = 0; i < sheetRows.length; i++) {
  const row = sheetRows[i].json;
  const email = (row.Email || "").trim().toLowerCase();

  if (email === emailToFind) {
    matchedRow = row;
    matchIndex = i;
    break;
  }
}

if (matchIndex === -1) {
  throw new Error("Không tìm thấy email trong sheet.");
}

let url1 = matchedRow["URL1"]?.trim() || "";
let url2 = matchedRow["URL2"]?.trim() || "";
let url3 = matchedRow["URL3"]?.trim() || "";

const clickCount = parseInt(matchedRow["Lượt nhấp"]) || 0;

const urlAlreadyExists = [url1, url2, url3].includes(currentUrl);

if (!urlAlreadyExists) {
  if (!url1) {
    url1 = currentUrl;
  } else if (!url2) {
    url2 = currentUrl;
  } else if (!url3) {
    url3 = currentUrl;
  }

}

return [{
  json: {
    rowIndex: matchedRow.row_number,
    LuotClick: clickCount + 1,
    url1,
    url2,
    url3,
    email: matchedRow.Email,
    name: matchedRow["Họ Tên"],
    campaign: matchedRow["Campaign"]
  }
}];

const emailToFind = $items("SET_TT")[0].json.email.trim().toLowerCase();

let matchIndex = -1;
let matchedRow = null;

const sheetItems = $items("Data_Sheet");

for (let i = 0; i < sheetItems.length; i++) {
  const emailInRow = (sheetItems[i].json.Email || "").trim().toLowerCase();

  if (emailInRow === emailToFind) {
    matchIndex = i;
    matchedRow = sheetItems[i].json;
    break;
  }
}

if (matchIndex !== -1) {
  return [
    {
      json: {
        found: true,
        matchIndex,
        row: matchedRow.row_number,
        lm: matchedRow["Lượt Mở"] || 0,
        ln: matchedRow["Lượt nhấp"] || 0,
        ipopen: matchedRow["IP1"] || "",
        ipnew: matchedRow["IP Khác"] || "",
        name: $items("SET_TT")[0].json.name,
        email: emailToFind,
        campaign: $items("SET_TT")[0].json.campaign,
      },
    },
  ];
} else {
  return [
    {
      json: {
        found: false,
        matchIndex: -1,
        name: $items("SET_TT")[0].json.name,
        email: emailToFind,
        campaign: $items("SET_TT")[0].json.campaign,
      },
    },
  ];
}

